package jpexs.decompiler.app;

import jpexs.decompiler.list.LinkedList;

import static jpexs.decompiler.utilities.StringUtils.join;
import static jpexs.decompiler.utilities.StringUtils.split;
import static jpexs.decompiler.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
